# Questão 3
# conjunto de 5 dígitos consecutivos na sequência abaixo que gere o maior produto

sequencia = (
    ""
)

maior_produto = 0
sequencia_maior_produto = ""

for i in range(len(sequencia) - 4):
    grupo = sequencia[i:i+5]
    produto = 1
    for digito in grupo:
        produto *= int(digito)
    if produto > maior_produto:
        maior_produto = produto
        sequencia_maior_produto = grupo

print('A Sequência de 5 dígitos com o maior produto é \033[1;34m{}\033[m'.format(sequencia_maior_produto))
print('Calculando o produto passo a passo:')

produto_parcial = 1
for i, digito in enumerate(sequencia_maior_produto):
    digito_int = int(digito)
    produto_anterior = produto_parcial
    produto_parcial *= digito_int
    print(f"{produto_anterior} × {digito_int} = {produto_parcial}")

print('\nProduto final da sequência \033[1;34m{}\033[m é \033[1;31m{}\033[m '.format(sequencia_maior_produto, produto_parcial))


